    private void ButtonCalcularMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ButtonCalcularMouseClicked
int sueldo=0,bono=0,rete=0,total=0;
switch(this.ComboBox1.getSelectedIndex()){
    case 0:
    switch(this.ComboBox2.getSelectedIndex()){
        case 0:
        sueldo=1200;
        break;
        case 1:
        sueldo=1400;
        break;
        case 2:
        sueldo=1600;
        break;
    }
    case 1:
    switch(this.ComboBox2.getSelectedIndex()){
        case 0:
        sueldo=700;
        break;
        case 1:
        sueldo=800;
        break;
        case 2:
        sueldo=1000;
        break;
    }
}
//bono
switch(this.ComboBox3.getSelectedIndex()){
    case 0:
    bono=sueldo*25/100;
    break;
    case 1:
    bono=sueldo*20/100;
    break;
    case 2:
    bono=sueldo*18/100;
    break;
    case 3:
    bono=sueldo*15/100;
    break;
    case 4:
    bono=sueldo*12/100;
    break;
    case 5:
    bono=sueldo*10/100;
    break;
}
//retenciones
switch(this.ComboBox2.getSelectedIndex()){
    case 0:
    rete=sueldo*12/100;
    break;
    case 1:
    rete=sueldo*8/100;
    break;
    case 2:
    rete=sueldo*6/100;
    break;
}
total=bono+sueldo+rete;
this.TextField3.setText("L."+sueldo);
this.TextField4.setText("L."+bono);
this.TextField5.setText("L."+rete);
this.TextField6.setText("L."+total);

    }//GEN-LAST:event_ButtonCalcularMouseClicked

